import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent implements OnInit {
  visibileAnagrafica = false;
  visibileStudi = false;
  visibileLavori = false;
  visibile = false;

  constructor(/*parametri per esempio apiService*/) {}

  ngOnInit(): void {
    /*this.apiService: ApiService*/
  }

  cambiaVisibilitaAnagrafica(){
    this.visibileAnagrafica = !this.visibileAnagrafica;
  }

  cambiaVisibilitaStudi(){
    this.visibileStudi = !this.visibileStudi;
  }

  cambiaVisibilitaLavori(){
    this.visibileLavori = !this.visibileLavori;
  }

  //...............

  cambiaVisibilita2(nomeSezione: string){
    this.visibileStudi = false;
      this.visibileAnagrafica = false;
      this.visibileLavori = false;
    if(nomeSezione == "visibileAnagrafica"){
      this.visibileAnagrafica = true;
    }
    else if(nomeSezione == "visibileLavori"){
      this.visibileLavori = true;
    }
    else
    {
      this.visibileStudi = true;
    }
    // this.nomeSezione = !this.nomeSezione;
    // if(this.visibileAnagrafica){
    //   this.visibileLavori = false;
    //   this.visibileStudi = false;
    // }
  }

  //...............

  cambiaVisibilitaTotale(){
    
    this.visibileAnagrafica = !this.visibileAnagrafica;
    this.visibileStudi = !this.visibileStudi;
    this.visibileLavori = !this.visibileLavori;
  }
}
