<app-top-bar></app-top-bar>

<div class="container">
    <router-outlet></router-outlet>
</div>


{{ visibile }}
{{ utente }}  <!-- converte in stringa  -->
{{ utente | json}} <!-- mostra l'oggetto proprietà valore -->

<!-- stampa il valore di visibile -->
<button (click)="visibile = !visibile">MOSTRA</button>

<div *ngIf="visibile">MIAO</div>



