import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  visibile = false;

  voto = 20;
  
  utente = {
    nome: "mario",
    cognome: "rossi"
  }

  cambiaVisibilita(){
    this.visibile = !this.visibile;
  }
}

/*
