can be found in the LICENSE file at https://angular.io/license
*/
<app-top-bar></app-top-bar>

<div class="container">
    <router-outlet></router-outlet>
</div>

{{ visibile }}
{{ utente }}
<!-- converte in stringa  -->
{{ utente | json }}
<!-- mostra l'oggetto proprietà valore -->

<!-- stampa il valore di visibile -->
<!-- <button (click)="visibile = !visibile">MOSTRA</button> -->
<!-- 
  altra versione per fare la stessa cosa, vado a richiamare la funzione creata 
  precedentemente in app.components.ts 
-->
<button (click)="cambiaVisibilita()"
  [disabled]="visibile">
  <!-- {{ visibile?"NASCONDI":"MOSTRA" }} per cambiare la scritta del bottone soluzione mmm -->
  <span *ngIf="visibile">NASCONDI</span>
  <span *ngIf="!visibile">MOSTRA</span>
</button>

<div *ngIf="visibile">MIAO</div>

<!-- questo per aggiungere classi in base ad una condizione -->
<div [class.insufficiente]="voto < 18 " 
     [class.sufficiente]="voto >= 18 " [class.ottimo]="voto > 28 "></div>

